import api from '../api/subsEpisodes'

export const GET_SUBS_EPISODE = 'GET_SUBS_EPISODE';

export function getSubsEpisodesRequest() {
    return {
        type: GET_SUBS_EPISODE
    }
}

export function getSubsEpisodesSuccess(message) {
    return {
        type: GET_SUBS_EPISODE,
        status: 'success',
        message: message
    }
}

export function getSubsEpisodesFailure(error) {
    return {
        type: GET_SUBS_EPISODE,
        status: 'error',
        error: error
    }
}

export default function getSubsEpisodes(numberOfEpisodes , fromEpisode) {
    return dispatch => {
        dispatch(getSubsEpisodesRequest());
        api.getSubsEpisodes(numberOfEpisodes , fromEpisode)
            .then(episodes => dispatch(getSubsEpisodesSuccess(episodes)))
            .catch(error => dispatch(getSubsEpisodesFailure(error)))
    }
}