import React from 'react'

export default class Episode extends React.Component {
    render(){
        const { episode } = this.props;
        return(
            <div className="large-6 large-centered columns panel">
                <div className="row"> Сериал: {episode.serial.title}</div>
                <div className="row"> Перевод: {episode.studio.name}</div>
                <div className="row"> Сезон: {episode.seasonNumber}</div>
                <div className="row"> Серия: {episode.episodeNumber}</div>
                <div className="row"> Дата: {episode.date}</div>
                <div className="row"> Ссылка: {episode.link}</div>
            </div>
        )
    }
}