import React from 'react'

export default class Episode extends React.Component {
    render(){
        const { episode } = this.props;
        return(
            <div className="panel callout">
                <div className="row"> <h4>{episode.serial.title}</h4></div>
                <div className="row"> Сезон: {episode.seasonNumber}</div>
                <div className="row"> Серия: {episode.episodeNumber}</div>
                <div className="row"> Дата: {episode.date}</div>
                <div className="row"> Ссылка: <a href={episode.link} target="_blank"> {episode.studio.name}</a></div>
            </div>
        )
    }
}