import React from 'react'
import Episode from './Episode'

export default class Feed extends React.Component {

    render(){
        const { episodes }  = this.props;

        if (episodes.isLoading !== true) {
            if (typeof episodes.message !== `undefined`){
                return (
                    <div className="row">
                        {episodes.message.map(function(episode) {
                            return <Episode episode={episode} />;
                        })};
                    </div>
                )
            }
            else return (
                <div className="row">{episodes.error}</div>
            )
        } else {
            return (
                <div className="row">Загрузка...</div>
            )
        }
    }

}