import React from 'react'
import { Link } from 'react-router'

export default class Header extends React.Component {
    render() {
        const { profile } = this.props;

        return (
            <div className="contain-to-grid">
                <nav className="top-bar" data-topbar role="navigation">

                    <ul className="title-area">
                        <li className="name">
                            <h1><Link to="/">NetCracker Serials</Link></h1>
                        </li>
                    </ul>


                    <section className="top-bar-section">
                        { profile ?
                            <MiniProfile profile={profile}/> :
                            <LoginButton />
                        }

                        <ul className="left">
                            <li className=""><Link to="/">All</Link></li>


                            {(() => {
                                if (profile) {
                                    return (
                                        <span>
                                    <li className=""><Link to="/favourites">Favourites</Link></li>
                                    </span>
                                    )
                                }
                            })()}
                        </ul>


                    </section>

                </nav>
            </div>
        );
    }
}

class MiniProfile extends React.Component {
    render() {
        const { profile } = this.props;

        return (
            <ul className="right">
                <li className="active"><Link to="/subscriptions">Subscriptions</Link></li>
                <li>
                    <Link to="/auth/profile">{profile.email}</Link>
                </li>
            </ul>
        )
    }
}

class LoginButton extends React.Component {
    render() {
        return (
            <ul className="right">
                <li><Link to="/auth/login">Login</Link></li>
            </ul>
        )
    }
}