import React from 'react'
import { Link } from 'react-router'

export default class LoginForm extends React.Component {
    handleLoginClick() {
        const credentials = {
            email: this.refs.email.value,
            password: this.refs.password.value
        };
        this.props.onLoginClick(credentials)
        //this.refs.password.value = '';
    }

    render() {
        return (
            <div className="large-5 large-centered columns">
                <div className="login-box">
                    <div className="row">
                        <div className="large-12 columns">
                            <form>
                                <div className="row">
                                    <div className="large-12 columns">
                                        <input type="email" ref="email" placeholder="Email" />
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="large-12 columns">
                                        <input type="password" ref="password" placeholder="Password" />
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="large-12 large-centered columns">
                                        <button type="button" className="button expand" onClick={() => this.handleLoginClick()}>Sign in</button>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="large-12 large-centered columns">
                                        <Link to='/auth/register' className="button expand">Register</Link>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
