import React from 'react'
import { Link } from 'react-router'

export default class Registration extends React.Component {
    handleRegisterClick() {
        const credentials = {
            email: this.refs.email.value,
            password: this.refs.password.value
        };
        this.props.onRegisterClick(credentials)
        this.refs.password.value = '';
    }

    render() {
        const { message, error } = this.props.auth;

        return (
            <div className="large-5 large-centered columns">
                <div className="login-box">
                    <div className="row">
                        <div className="large-12 columns">
                            <form>
                                {() => {
                                    if (error || message) {
                                        return (
                                            <div className="row">
                                                <div className="large-12 columns">
                                                    <h4>{error || message}</h4>
                                                </div>
                                            </div>
                                        )
                                    }
                                }}
                                <div className="row">
                                    <div className="large-12 columns">
                                        <input type="email" ref="email" placeholder="Email"/>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="large-12 columns">
                                        <input type="password" ref="password" placeholder="Password"/>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="large-12 large-centered columns">
                                        <button type="button" className="button expand"
                                                onClick={() => this.handleRegisterClick()}>Sign up
                                        </button>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="large-12 large-centered columns">
                                        <Link to='/auth/login' className="button expand">Cancel</Link>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}