import React from 'react';
import { connect } from 'react-redux';
import Episode from 'components/Episode';
import Feed from 'components/Feed';
import { getEpisodes } from '../actions/episodesActions';

@connect(state => state)
export default class AllEpisodes extends React.Component {
    render() {
      var { generalEpisodes, isLoading, error } = this.props.episodes;
      var { dispatch } = this.props;

      return (
        <div>
          <Feed episodes={generalEpisodes} isLoading={isLoading} error={error} addEpisodes={from => dispatch(getEpisodes(9, from, false))}/>
        </div>
      );
    }
};
