import React from 'react'
import { connect } from 'react-redux'
import Episode from 'components/Episode'
import getEpisodes from 'actions/episodesActions'
import Feed from 'components/Feed'


@connect(state => state)
export default class FavouriteEpisodes extends React.Component {

    render() {
        var { favouriteEpisodes , isLoading, error } = this.props.episodes;
        return (
            <div>
                <Feed episodes={favouriteEpisodes} isLoading={isLoading} error={error}/>
            </div>
        )
    }
}