import { GET_GENERAL_EPISODES, GET_FAVOURITE_EPISODES, REMOVE_FAVOURITE_EPISODES } from '../actions/episodesActions'

const DEFAULT_STATE = {
    isLoading: false
}

export default function episodes(state = DEFAULT_STATE, action) {
    const {type, status, message, error} = action;

    switch (type){
        case GET_GENERAL_EPISODES:
            if (!status){
                return { ...state, isLoading: true}
            }
            if (status === 'success'){
                return { ...state, generalEpisodes: message, isLoading: false}
            }
            else {
                return { ...state, error: error, isLoading: false}
            }
        case GET_FAVOURITE_EPISODES:
            if (!status){
                return { ...state, isLoading: true}
            }
            if (status === 'success'){
                return { ...state, favouriteEpisodes: message, isLoading: false}
            }
            else {
                return { ...state, error: error, isLoading: false}
            }
        case REMOVE_FAVOURITE_EPISODES:
            state.favouriteEpisodes = null;
            return state;
        default: return state;
    }
}