import { GET_EPISODE } from '../actions/episodesActions'

const DEFAULT_STATE = {

}

export default function episodes(state = DEFAULT_STATE, action) {
    const {type, status, message, error} = action;

    switch (type){
        case GET_EPISODE:
            if (!status){
                return { ...state, isLoading: true}
            }
            if (status === 'success'){
                return { ...state, message: message, isLoading: false}
            }
            else {
                return { ...state, error: error, isLoading: false}
            }
            default: return state;
    }
}