import store from 'store';

export function requireLogin(nextState, replaceState) {
  if (!store.getState().auth.profile) {
    replaceState({
      nextPathname: nextState.location.pathname,
    }, '/auth/login');
  }
}

export function checkSubscriptionByTokenId(tokenId) {
  return store.getState().subscriptions.tokens.find(token => token.id === tokenId);
};
